import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-chatbot',
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent {
  messages: { text: string, sender: string }[] = [];
  userMessage: string = '';
  isLoading: boolean = false;

  constructor(private http: HttpClient) { }
  isChatMessagesScrollable(): boolean {
    const chatMessagesElement = document.querySelector('.chat-messages');
    return chatMessagesElement.scrollHeight > chatMessagesElement.clientHeight;
  }

  startNewChat() {
    this.messages = [];
    this.userMessage = '';
  }

  sendMessage(message: string) {
    this.messages.push({ text: message, sender: 'user' });
    this.isLoading = true;
    const formData = new FormData();
    formData.append('question', message);

    this.http.post('http://3.212.224.14:8000/chat/', formData )
      .subscribe((response: any) => {
          console.log("Answer", response);
          const answer = response.answer;
          this.messages.push({ text: answer, sender: 'bot' });
          this.isLoading = false;
        },
        (error: any) => {
          console.error('Error:', error);
          this.isLoading = false;
        }
      );

    this.userMessage = '';
  }

  getMessageClass(message: any): string {
    if (message.sender === 'user') {
      return 'user-message';
    } else {
      return 'bot-message';
    }
  }

  getInputPlaceholder(): string {
    return this.messages.length === 0 ? 'Hello! How can I help you?' : 'Type a Message';
  }
}
<section class="chatbot">
  <div class="chat-container">
    <aside class="sidebar">
      <button class="btn btn-primary" (click)="startNewChat()">New Chat</button>
    </aside>
    <div class="chat-side px-5">
      <div class="chat-messages" [ngClass]="{'scrollable': isChatMessagesScrollable()}" [class.empty-chat]="messages.length === 0">
        <ng-container *ngIf="messages.length === 0">
          <h1 class="text-center text-dark">Welcome to Chatbot</h1>
          <div class="chat-list">
            <div class="chat-list-box">
              <h2 class="flex gap-3 items-center m-auto text-lg font-normal md:flex-col md:gap-2">
                <svg stroke="currentColor" fill="none" stroke-width="1.5" viewBox="0 0 24 24" stroke-linecap="round" stroke-linejoin="round" class="h-6 w-6" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg"><circle cx="12" cy="12" r="5"></circle><line x1="12" y1="1" x2="12" y2="3"></line><line x1="12" y1="21" x2="12" y2="23"></line><line x1="4.22" y1="4.22" x2="5.64" y2="5.64"></line><line x1="18.36" y1="18.36" x2="19.78" y2="19.78"></line><line x1="1" y1="12" x2="3" y2="12"></line><line x1="21" y1="12" x2="23" y2="12"></line><line x1="4.22" y1="19.78" x2="5.64" y2="18.36"></line><line x1="18.36" y1="5.64" x2="19.78" y2="4.22"></line></svg>Examples</h2>
                <ul class="flex flex-col w-full sm:max-w-md m-auto">
                  <button class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md hover:bg-gray-200 dark:hover:bg-gray-900">"Explain quantum computing in simple terms" →</button>
                  <button class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md hover:bg-gray-200 dark:hover:bg-gray-900">"Got any creative ideas for a 10 year old’s birthday?" →</button>
                  <button class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md hover:bg-gray-200 dark:hover:bg-gray-900">"How do I make an HTTP request in Javascript?" →</button>
                </ul>
            </div>
            <div class="chat-list-box">
              <h2 class="flex gap-3 items-center m-auto text-lg font-normal md:flex-col md:gap-2">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true" class="h-6 w-6"><path stroke-linecap="round" stroke-linejoin="round" d="M3.75 13.5l10.5-11.25L12 10.5h8.25L9.75 21.75 12 13.5H3.75z"></path></svg>Capabilities</h2>
                <ul class="flex flex-col w-full sm:max-w-md m-auto">
                  <li class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md">Remembers what user said earlier in the conversation</li>
                  <li class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md">Allows user to provide follow-up corrections</li>
                  <li class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md">Trained to decline inappropriate requests</li>
                </ul>
            </div>
            <div class="chat-list-box">
              <h2 class="flex gap-3 items-center m-auto text-lg font-normal md:flex-col md:gap-2">
                <svg stroke="currentColor" fill="none" stroke-width="1.5" viewBox="0 0 24 24" stroke-linecap="round" stroke-linejoin="round" class="h-6 w-6" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg"><path d="M10.29 3.86L1.82 18a2 2 0 0 0 1.71 3h16.94a2 2 0 0 0 1.71-3L13.71 3.86a2 2 0 0 0-3.42 0z"></path><line x1="12" y1="9" x2="12" y2="13"></line><line x1="12" y1="17" x2="12.01" y2="17"></line></svg>
                Limitations</h2>
                <ul class="flex flex-col w-full sm:max-w-md m-auto">
                  <li class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md">May occasionally generate incorrect information</li>
                  <li class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md">May occasionally produce harmful instructions or biased content</li><li class="w-full bg-gray-50 dark:bg-white/5 p-3 rounded-md">Limited knowledge of world and events after 2021</li>
                </ul>
            </div>
          </div>
        </ng-container>
        <div *ngFor="let message of messages" [ngClass]="getMessageClass(message)">
          <ng-container *ngIf="message.sender === 'user'">
            <div class="user-messages">
              <span class="user-icon">S </span>{{ message.text }}
            </div>
          </ng-container>
          <ng-container *ngIf="message.sender === 'bot'">
            <div class="bot-messages">
              <img src="../../assets/chat-logo.png" alt="Bot Avatar" class="bot-icon">
              {{ message.text }}
            </div>
          </ng-container>
        </div>
        <div *ngIf="isLoading" class="loader">
          <div class="dot"></div>
          <div class="dot"></div>
          <div class="dot"></div>
        </div>
      </div>
      <div class="input-group mb-3">
        <hr>
        <input type="text" class="form-control" [(ngModel)]="userMessage" [placeholder]="getInputPlaceholder()" (keyup.enter)="sendMessage(userMessage)" />
        <button class="btn btn-primary" (click)="sendMessage(userMessage)">
          <svg id="ic_send" fill="#FFFFFF" height="24" viewBox="0 0 24 24" width="24" xmlns="http://www.w3.org/2000/svg" aria-hidden="true"><path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"></path><path d="M0 0h24v24H0z" fill="none"></path></svg>
        </button>
      </div>
    </div>
  </div>
</section>
