import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-chatbot',
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.scss']
})
export class ChatbotComponent {
  messages: { text: string, sender: string }[] = [];
  userMessage: string = '';

  constructor(private http: HttpClient) { }

  isChatMessagesScrollable(): boolean {
    const chatMessagesElement = document.querySelector('.chat-messages');
    return chatMessagesElement.scrollHeight > chatMessagesElement.clientHeight;
  }

  // sendMessage(message: string) {
  //   this.messages.push({ text: message, sender: 'user' });

  //   let response: string;

  //   if (message.toLowerCase().includes('hello')) {
  //     response = 'Hello there!';
  //   } else if (message.toLowerCase().includes('how are you')) {
  //     response = 'I am doing well, thank you!';
  //   } else if (message.toLowerCase().includes('goodbye')) {
  //     response = 'Goodbye! Take care.';
  //   } else {
  //     response = "I'm sorry, I didn't understand that.";
  //   }

  //   this.messages.push({ text: response, sender: 'bot' });
  //   this.userMessage = '';
  // }

  sendMessage(message: string) {
    this.messages.push({ text: message, sender: 'user' });

    this.http.post('http://52.91.18.190:8000/chat/', { params: { question: message } })
      .subscribe((response: any) => {
          console.log("Answer", response);
          const answer = response;
          this.messages.push({ text: answer, sender: 'bot' });
        },
        (error: any) => {
          console.error('Error:', error);
        }
      );

    this.userMessage = '';
  }

  getMessageClass(message: any): string {
    if (message.sender === 'user') {
      return 'user-message';
    } else {
      return 'bot-message';
    }
  }
}
<section class="chatbot">
  <div class="container chat-container">
    <div class="chat-header">
      <h1>Chatbot</h1>
      <p class="mb-0">How can we assist you?</p>
      <div class="offline-message" style="background-color: rgba(77, 78, 79, 0.72);">
        <span class="online"><span>Online Now</span></span>
      </div>
    </div>

    <div class="chat-messages" [ngClass]="{ 'scrollable': isChatMessagesScrollable() }">
      <div *ngFor="let message of messages" [ngClass]="getMessageClass(message)">
        {{ message.text }}
      </div>
    </div>
    <div class="input-group mb-3">
      <hr>
      <input type="text" class="form-control" [(ngModel)]="userMessage" placeholder="Type a message..." (keyup.enter)="sendMessage(userMessage)" />
      <button class="btn btn-primary" (click)="sendMessage(userMessage)">
        <svg id="ic_send" fill="#FFFFFF" height="24" viewBox="0 0 24 24" width="24" xmlns="http://www.w3.org/2000/svg" aria-hidden="true"><path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"></path><path d="M0 0h24v24H0z" fill="none"></path></svg>
      </button>
    </div>
  </div>
</section>
